    Description = {
        en = "Reprisal: Remove a journal entry from a quest.",
        de = "Vergeltung: Entfernt einen Tagebucheintrag vom Quest.",
        fr = "Rétribution: Supprime une entrée de journal de la quête.",
    },
    Parameter = {
